pragma solidity ^0.4.23;

import "./zueTokenSale.sol";
import "./standardContracts/ownership/Ownable.sol";

contract zueTokenRefund is Ownable {
  zueTokenSale sale;
  uint256 public depositedValue;

  struct finalDeposit{
    uint256 amount;
    bool refunded;
  }

  enum State {refundInactive, refundActive, refundClosed}
  //public method for mapping finalDeposit
  mapping (address => finalDeposit) public finalDeposits;
  State public state;

  event RefundsClosed();
  event RefundsEnabled();
  event Refunded(address indexed refundedAddress, uint256 value);

  constructor(address saleAddress) public {
    require(saleAddress != address(0));
    sale = zueTokenSale(saleAddress);
    state = State.refundInactive;
  }

  function () payable public {}
  
  // Method for withdrawal in the case of failure of the ICO
  function refund() public {
    require(state == State.refundActive);
    require(finalDeposits[msg.sender].amount == 0);
    require(!finalDeposits[msg.sender].refunded);
    finalDeposits[msg.sender].refunded=true;
    depositedValue = sale.getDeposits(msg.sender);
    finalDeposits[msg.sender].amount=depositedValue;
    msg.sender.transfer(depositedValue);
    emit Refunded(msg.sender, depositedValue);
  }

  //enabling refunds
  function enableRefunds() onlyOwner public {
    require(state == State.refundInactive);
    state = State.refundActive;
    emit RefundsEnabled();
  }

  //closing refunds if needed
  function closeRefunds() onlyOwner public {
    require(state == State.refundActive);
    state = State.refundClosed;
    emit RefundsClosed();
  }

  function getDeposits(address userAddr) public constant returns (uint256){
    require(userAddr != address(0));
    uint256 val = sale.getDeposits(userAddr);
    return val;
  }
}
